##########################
# deletecfg.pl
##########################

use ElectricCommander;
use ElectricCommander::PropDB;

use constant {
    SUCCESS => 0,
    ERROR   => 1,
};

my $opts;

my $PLUGIN_NAME = "EC-HelloWorld";
my $projName    = "EC-HelloWorld-1.0.0.0";

if ( !defined $PLUGIN_NAME ) {
    print "PLUGIN_NAME must be defined\n";
    exit ERROR;
}

## get an EC object
my $ec = new ElectricCommander();
$ec->abortOnError(0);

my $opts;
$opts->{config} = "$[config]";

if ( !defined $opts->{config} || "$opts->{config}" eq "" ) {
    print "config parameter must exist and be non-blank\n";
    exit ERROR;
}

# check to see if a config with this name already exists before we do anything else
my $xpath = $ec->getProperty("/myProject/helloworld_cfgs/$opts->{config}");
my $property = $xpath->findvalue("//response/property/propertyName");

if ( !defined $property || "$property" eq "" ) {
    my $errMsg =
      "Error: A configuration named '$opts->{config}' does not exist.";
    $ec->setProperty( "/myJob/configError", $errMsg );
    print $errMsg;
    exit ERROR;
}

$ec->deleteProperty("/myProject/helloworld_cfgs/$opts->{config}");
$ec->deleteCredential( $projName, $opts->{config} );
exit SUCCESS;

